import { useState } from 'react';
import './App.css';
//import About from './components/About';
import Navbar from './components/Navbar';
//import TextForm from './components/TextForm';
//import Alert from './components/Alert';

function App() {
  const [mode, setMode] = useState({
    color: 'dark',
    bgColor: 'dark'
  }); //Whether dark mode is enabled or not.

  //const [alert, setAlert] = useState(null);

  // const showAlert = (message, type) => {
  //   setAlert({
  //     message: message,
  //     type: type
  //   });
  //   setTimeout(() => {
  //     setAlert(null);
  //     }, 2000);
  // }

  const toggleMode = () => {
    //console.log(theme);
    if (mode.color === 'dark') {
        setMode({color: 'dark', bgColor: 'dark'});
        document.body.style.backgroundColor = '#162024';
        document.body.style.color = 'white';
        //showAlert("Dark mode has been enabled", "success");
    }
    if (mode.color === 'primary') {
      setMode({color: 'dark', bgColor: 'primary'});
      document.body.style.backgroundColor = '#3388FF';
      document.body.style.color = 'white';
      //showAlert("Primary mode has been enabled", "success");
    }
    else{
      setMode({color: 'light', bgColor: 'light'});
      document.body.style.backgroundColor = 'white';
      document.body.style.color = 'black';
      //showAlert("Light mode has been enabled", "success");
    }
  }

  return (
    <>
      <Navbar title="Textutils" mode={mode} toggleMode={toggleMode}/>
      {/* <Alert alert={alert}/> */}
      <div className='container my-3'>
        {/* <TextForm heading="Enter the text to analyze below" mode={mode} showAlert={showAlert}/> */}
        {/* <About/> */}
      </div>
    </>
  );
}

export default App;
