import React, {useState} from 'react'
import PropTypes from 'prop-types'

export default function TextForm(props) {
    const handleUppercase = () => {
        let newText = text.toUpperCase();
        setText(newText);
    }

    const handleLowercase = () => {
        setText(text.toLowerCase());
    }

    const handleOnChange = (event) => {
        setText(event.target.value);
    }

    const handleClear = () => {
        setText("");
    }

    const [text, setText] = useState('');
  return (
    <>
        <div className='container'>
        <h1>{props.heading}</h1>
        <div className="mb-3">
        <textarea className="form-control" value={text} id="txtarea" onChange={handleOnChange} rows="8" placeholder={text}></textarea>
        </div>
        <button type="button" className="btn btn-primary me-2" onClick={handleUppercase}>Convert to Uppercase</button>
        <button type="button" className="btn btn-primary" onClick={handleLowercase}>Convert to Lowercase</button>
        <button type="button" className="btn btn-primary" onClick={handleClear}>Clear</button>
        </div>
        <div className='container my-3'>
            <h2>Your text summary</h2>
            <p>{(text.length == 0) ? 0:text.split(" ").length} words, {text.length} characters</p>
            <p>{text.split(" ").length * 0.008} minutes read</p>
            <h2>Preview</h2>
            <p>{text}</p>
        </div>
    </>
  )
}
TextForm.propTypes = {heading: PropTypes.string.isRequired};
TextForm.defaultProps = {text: 'Enter text here'}
